// Extract and score leads from all sources
const leads = [];
const today = new Date().toISOString().split('T')[0];

for (const input of $input.all()) {
  const data = input.json;

  // Handle LinkedIn Jobs
  if (data.jobs_results) {
    for (const job of data.jobs_results || []) {
      const company = job.company_name || 'Unknown';
      const title = job.title || '';
      const location = job.location || '';

      // Score based on job characteristics
      let score = 3; // Base score for job posting
      if (title.toLowerCase().includes('senior') || title.toLowerCase().includes('lead')) score += 2;
      if (title.toLowerCase().includes('manager') || title.toLowerCase().includes('director')) score += 3;
      if (company.toLowerCase().includes('tech') || company.toLowerCase().includes('software')) score += 1;

      leads.push({
        json: {
          date_found: today,
          company_name: company,
          signal_type: 'hiring',
          source: 'LinkedIn',
          evidence: title,
          link: job.share_link || '',
          location: location,
          score: score,
          unique_id: `${company.replace(/[^a-zA-Z0-9]/g, '_')}_hiring_${today}`
        }
      });
    }
  }

  // Handle Funding/Expansion News
  if (data.organic_results) {
    for (const result of data.organic_results || []) {
      const title = result.title || '';
      const snippet = result.snippet || '';
      const text = `${title} ${snippet}`.toLowerCase();

      // Extract company name from title
      let company = 'Unknown';
      const companyMatch = title.match(/^([A-Z][a-zA-Z\s&\.]+?)\s+(raises|raised|secures|closes|expands|announces)/i);
      if (companyMatch) company = companyMatch[1].trim();

      // Score based on funding/expansion signals
      let score = 2;
      let signalType = 'news';

      if (text.includes('series a') || text.includes('series b')) {
        score = 6;
        signalType = 'funding';
      } else if (text.includes('raised') || text.includes('funding')) {
        score = 5;
        signalType = 'funding';
      } else if (text.includes('expansion') || text.includes('opening office')) {
        score = 4;
        signalType = 'expansion';
      }

      if (score >= 4) { // Only add high-value signals
        leads.push({
          json: {
            date_found: today,
            company_name: company,
            signal_type: signalType,
            source: 'Google News',
            evidence: title,
            link: result.link || '',
            location: '',
            score: score,
            unique_id: `${company.replace(/[^a-zA-Z0-9]/g, '_')}_${signalType}_${today}`
          }
        });
      }
    }
  }
}

return leads;

// Simple deduplication and filtering
const existingIds = new Set();
const newLeads = [];

// Collect existing IDs
for (const input of $input.all()) {
  if (input.json.unique_id) {
    existingIds.add(input.json.unique_id);
  } else if (Array.isArray(input.json)) {
    // Handle sheet data
    input.json.forEach(row => {
      if (row.unique_id) existingIds.add(row.unique_id);
    });
  }
}

// Filter new leads
for (const input of $input.all()) {
  if (input.json.signal_type && !existingIds.has(input.json.unique_id)) {
    // Apply basic filters
    const company = input.json.company_name || '';
    const score = input.json.score || 0;

    // Skip if company name is too generic or score too low
    if (company.length > 2 && 
        !company.toLowerCase().includes('unknown') && 
        score >= 3) {
      newLeads.push(input);
    }
  }
}

return newLeads;

// Merge enrichment data or pass through
const finalLeads = [];

for (const input of $input.all()) {
  let lead = { ...input.json };

  // Add enrichment if available
  if (lead.domain) {
    lead.website = lead.domain;
    lead.industry = lead.category?.industry || '';
    lead.employee_count = lead.metrics?.employees || '';
    lead.description = lead.description || '';
  } else {
    lead.website = '';
    lead.industry = '';
    lead.employee_count = '';
    lead.description = '';
  }

  // Clean final output
  finalLeads.push({
    json: {
      date_found: lead.date_found,
      company_name: lead.company_name,
      website: lead.website,
      signal_type: lead.signal_type,
      source: lead.source,
      evidence: lead.evidence,
      link: lead.link,
      location: lead.location,
      industry: lead.industry,
      employee_count: lead.employee_count,
      score: lead.score,
      unique_id: lead.unique_id
    }
  });
}

return finalLeads;

// Create simple daily summary
const leads = $input.all();
const total = leads.length;
const highPriority = leads.filter(l => l.json.score >= 5).length;
const funding = leads.filter(l => l.json.signal_type === 'funding').length;
const hiring = leads.filter(l => l.json.signal_type === 'hiring').length;

const topLeads = leads
  .sort((a, b) => b.json.score - a.json.score)
  .slice(0, 5)
  .map(l => `‚Ä¢ *${l.json.company_name}* (${l.json.signal_type}, score: ${l.json.score})`);

const message = `üéØ *Daily Lead Radar Report*

üìä *Today's Haul:*
‚Ä¢ Total leads: ${total}
‚Ä¢ High priority (‚â•5): ${highPriority}
‚Ä¢ Funding signals: ${funding}
‚Ä¢ Hiring signals: ${hiring}

üèÜ *Top Leads:*
${topLeads.join('\n')}

üìã <YOUR_GOOGLE_SHEET_URL|View Full Report>`;

return [{ json: { message, total, highPriority } }];
